    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        if(txtUsuarioI.getText().length()==0 || txtUsuarioI.getText().length()>45){
            JOptionPane.showMessageDialog(this, "debe ingresar un usuario y no debe exceder los 45 caracteres", "sistema", JOptionPane.WARNING_MESSAGE);
             txtUsuarioI.requestFocus();
            return;
        }
        
        if(txtClaveI.getText().length()==0 || txtClaveI.getText().length()>64){
            JOptionPane.showMessageDialog(this, "Debe ingresar una contrase√±a y no debe exceder los 64 caracteres", "sistema", JOptionPane.WARNING_MESSAGE);
             txtClaveI.requestFocus();
            return;
        }
        
        UsuarioControl CTRL = new UsuarioControl();
        String resp=CTRL.login(txtUsuarioI.getText(), txtClaveI.getText());
        if(resp.equals("1")){
            this.dispose();
            FrmPrincipal frm = new FrmPrincipal();
            frm.toFront();
            frm.setVisible(true);
        }else{
            JOptionPane.showMessageDialog(this, "Los datos ingresados son incorrectos", "sistema", JOptionPane.WARNING_MESSAGE);
            
        }
    }//GEN-LAST:event_jButton1ActionPerformed

